    // parameter. Note that constant size
    // doesn't necessarily mean secret.
    let z = 0;

    for (let i = 0; i < num.length; i++)
      z |= this.words[i % this.length] ^ num.words[i];

    z |= this.length ^ num.length;
    z |= this.negative ^ num.negative;

    return (z - 1) >>> 31;
