  assert((raw != null && raw._isBuffer === true));
  assert((prefix != null && prefix._isBuffer === true));
  assert(raw.length >= prefix.length);

  for (let i = 0; i < prefix.length; i++) {
    if (raw[i] !== prefix[i])
      return false;
  }

  return true;
